class Word

  attr_accessor :length, :first, :last, :words

  def initialize(length)
    @length = length
    @words = []
  end

  def add_word(word)
    @words << word
  end

  def find_word(word)
    @words.find do |word|
      word.to_s == word
    end
  end

  def add_word_to_first(word)
    @words.first = word
  end
